import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaUsers, FaChalkboardTeacher, FaBuilding, FaBook, 
  FaClipboardCheck, FaChartBar, FaCog, FaSignOutAlt,
  FaPlus, FaEdit, FaTrash, FaEye, FaSearch
} from 'react-icons/fa';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('overview');
  const [showAddModal, setShowAddModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [students, setStudents] = useState([]);
  const [faculty, setFaculty] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState('');

  const handleLogout = () => {
    navigate('/');
  };

  const handleAddClick = (type) => {
    setModalType(type);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setModalType('');
  };

  const navigationItems = [
    { id: 'overview', label: 'Overview', icon: FaChartBar, description: 'Key metrics at a glance' },
    { id: 'students', label: 'Students', icon: FaUsers, description: 'Manage student records and enrollment' },
    { id: 'faculty', label: 'Faculty', icon: FaChalkboardTeacher, description: 'Manage faculty profiles and assignments' },
    { id: 'departments', label: 'Departments', icon: FaBuilding, description: 'Department details and leads' },
    { id: 'courses', label: 'Courses', icon: FaBook, description: 'Catalog and course structure' },
    { id: 'attendance', label: 'Attendance', icon: FaClipboardCheck, description: 'Overview of attendance trends' },
    { id: 'reports', label: 'Reports', icon: FaChartBar, description: 'Analytics and insights' },
    { id: 'settings', label: 'Settings', icon: FaCog, description: 'System preferences and configuration' }
  ];

  const activeMeta = useMemo(() => navigationItems.find(n => n.id === activeSection) || navigationItems[0], [activeSection]);

  // Demo: simulate initial data fetch
  useEffect(() => {
    setLoading(true);
    const timer = setTimeout(() => {
      setStudents([
        { id: 1, name: 'Aarav Patel', email: 'aarav@tat.edu', department: 'CSE', year: '3' },
        { id: 2, name: 'Isha Sharma', email: 'isha@tat.edu', department: 'ECE', year: '2' },
        { id: 3, name: 'Rohit Das', email: 'rohit@tat.edu', department: 'ME', year: '4' },
      ]);
      setFaculty([
        { id: 1, name: 'Prof. Meera N', email: 'meera@tat.edu', department: 'CSE', position: 'Associate Prof.' },
        { id: 2, name: 'Dr. Ajay K', email: 'ajay@tat.edu', department: 'ECE', position: 'Professor' },
      ]);
      setDepartments([
        { id: 1, name: 'Computer Science', head: 'Dr. S. Rao', students: 420, faculty: 28 },
        { id: 2, name: 'Electronics', head: 'Dr. P. Nanda', students: 310, faculty: 22 },
      ]);
      setCourses([
        { id: 101, name: 'DSA', department: 'CSE', credits: 4, instructor: 'Prof. Meera N' },
        { id: 205, name: 'Signals', department: 'ECE', credits: 3, instructor: 'Dr. Ajay K' },
      ]);
      setLoading(false);
    }, 600);
    return () => clearTimeout(timer);
  }, []);

  const filteredStudents = useMemo(() => {
    if (!query) return students;
    const q = query.toLowerCase();
    return students.filter(s => (
      s.name.toLowerCase().includes(q) ||
      s.email.toLowerCase().includes(q) ||
      String(s.id).includes(q)
    ));
  }, [students, query]);

  const PageTitle = () => (
    <div className="page-title">
      <div className="page-title-left">
        <h1>{activeMeta.label}</h1>
        <p>{activeMeta.description}</p>
      </div>
      {activeSection !== 'overview' && (
        <div className="page-actions">
          {(activeSection === 'students' || activeSection === 'faculty' || activeSection === 'departments' || activeSection === 'courses') && (
            <button className="primary-btn" onClick={() => handleAddClick(activeSection.slice(0, -1))}>
              <FaPlus />
              Add {activeMeta.label.slice(0, -1)}
            </button>
          )}
        </div>
      )}
    </div>
  );

  const EmptyState = ({ title, subtitle }) => (
    <div className="empty-state">
      <h3>{title}</h3>
      <p>{subtitle}</p>
    </div>
  );

  const LoadingState = () => (
    <div className="loading-state">
      <div className="spinner" />
      <p>Loading {activeMeta.label.toLowerCase()}...</p>
    </div>
  );

  const SearchBar = ({ placeholder }) => (
    <div className="searchbar">
      <FaSearch />
      <input
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder={placeholder}
      />
    </div>
  );

  const renderOverview = () => (
    <div className="overview-section">
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon students">
            <FaUsers />
          </div>
          <div className="stat-content">
            <h3>{students.length}</h3>
            <p>Total Students</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon faculty">
            <FaChalkboardTeacher />
          </div>
          <div className="stat-content">
            <h3>{faculty.length}</h3>
            <p>Total Faculty</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon departments">
            <FaBuilding />
          </div>
          <div className="stat-content">
            <h3>{departments.length}</h3>
            <p>Departments</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon courses">
            <FaBook />
          </div>
          <div className="stat-content">
            <h3>{courses.length}</h3>
            <p>Courses</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderStudents = () => (
    <div className="section">
      <div className="section-toolbar">
        <SearchBar placeholder="Search by name, email or ID" />
      </div>
      <div className="table-container">
        <table className="data-table sticky">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
              <th>Year</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6"><LoadingState /></td></tr>
            ) : filteredStudents.length === 0 ? (
              <tr><td colSpan="6"><EmptyState title="No students found" subtitle="Try adjusting your search." /></td></tr>
            ) : (
              filteredStudents.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>{student.name}</td>
                  <td>{student.email}</td>
                  <td>{student.department}</td>
                  <td>{student.year}</td>
                  <td className="actions">
                    <button className="action-btn view"><FaEye /></button>
                    <button className="action-btn edit"><FaEdit /></button>
                    <button className="action-btn delete"><FaTrash /></button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderFaculty = () => (
    <div className="section">
      <div className="section-toolbar">
        <SearchBar placeholder="Search faculty by name, email or dept" />
      </div>
      <div className="table-container">
        <table className="data-table sticky">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
              <th>Position</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6"><LoadingState /></td></tr>
            ) : faculty.length === 0 ? (
              <tr><td colSpan="6"><EmptyState title="No faculty added" subtitle="Click Add Faculty to create a record." /></td></tr>
            ) : (
              faculty.map((member) => (
                <tr key={member.id}>
                  <td>{member.id}</td>
                  <td>{member.name}</td>
                  <td>{member.email}</td>
                  <td>{member.department}</td>
                  <td>{member.position}</td>
                  <td className="actions">
                    <button className="action-btn view"><FaEye /></button>
                    <button className="action-btn edit"><FaEdit /></button>
                    <button className="action-btn delete"><FaTrash /></button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderDepartments = () => (
    <div className="section">
      <div className="table-container">
        <table className="data-table sticky">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Head</th>
              <th>Students</th>
              <th>Faculty</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6"><LoadingState /></td></tr>
            ) : departments.length === 0 ? (
              <tr><td colSpan="6"><EmptyState title="No departments" subtitle="Click Add Department to create one." /></td></tr>
            ) : (
              departments.map((dept) => (
                <tr key={dept.id}>
                  <td>{dept.id}</td>
                  <td>{dept.name}</td>
                  <td>{dept.head}</td>
                  <td>{dept.students}</td>
                  <td>{dept.faculty}</td>
                  <td className="actions">
                    <button className="action-btn view"><FaEye /></button>
                    <button className="action-btn edit"><FaEdit /></button>
                    <button className="action-btn delete"><FaTrash /></button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderCourses = () => (
    <div className="section">
      <div className="table-container">
        <table className="data-table sticky">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Department</th>
              <th>Credits</th>
              <th>Instructor</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6"><LoadingState /></td></tr>
            ) : courses.length === 0 ? (
              <tr><td colSpan="6"><EmptyState title="No courses" subtitle="Click Add Course to create one." /></td></tr>
            ) : (
              courses.map((course) => (
                <tr key={course.id}>
                  <td>{course.id}</td>
                  <td>{course.name}</td>
                  <td>{course.department}</td>
                  <td>{course.credits}</td>
                  <td>{course.instructor}</td>
                  <td className="actions">
                    <button className="action-btn view"><FaEye /></button>
                    <button className="action-btn edit"><FaEdit /></button>
                    <button className="action-btn delete"><FaTrash /></button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderAttendance = () => (
    <div className="attendance-section">
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Today's Attendance</h3>
          <p className="metric">85%</p>
        </div>
        <div className="stat-card">
          <h3>This Week</h3>
          <p className="metric">92%</p>
        </div>
        <div className="stat-card">
          <h3>This Month</h3>
          <p className="metric">88%</p>
        </div>
      </div>
    </div>
  );

  const renderReports = () => (
    <div className="reports-section">
      <div className="reports-grid">
        <div className="report-card">
          <h3>Student Performance</h3>
          <p>View detailed performance reports</p>
        </div>
        <div className="report-card">
          <h3>Attendance Reports</h3>
          <p>Generate attendance reports</p>
        </div>
        <div className="report-card">
          <h3>Financial Reports</h3>
          <p>View financial summaries</p>
        </div>
      </div>
    </div>
  );

  const renderSettings = () => (
    <div className="settings-section">
      <div className="settings-grid">
        <div className="setting-card">
          <h3>General Settings</h3>
          <p>Configure general system settings</p>
        </div>
        <div className="setting-card">
          <h3>User Management</h3>
          <p>Manage user accounts and permissions</p>
        </div>
        <div className="setting-card">
          <h3>Backup & Restore</h3>
          <p>System backup and restore options</p>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return renderOverview();
      case 'students':
        return renderStudents();
      case 'faculty':
        return renderFaculty();
      case 'departments':
        return renderDepartments();
      case 'courses':
        return renderCourses();
      case 'attendance':
        return renderAttendance();
      case 'reports':
        return renderReports();
      case 'settings':
        return renderSettings();
      default:
        return renderOverview();
    }
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo"><span>TAT</span></div>
            <h1>Admin Panel</h1>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <FaSignOutAlt />
            <span>Logout</span>
          </button>
        </div>
      </header>

      <nav className="horizontal-nav">
        {navigationItems.map((item) => (
          <button
            key={item.id}
            className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
            onClick={() => setActiveSection(item.id)}
            aria-label={`Go to ${item.label}`}
          >
            <item.icon />
            <span>{item.label}</span>
          </button>
        ))}
      </nav>

      <main className="main-content">
        <div className="dashboard-content">
          <PageTitle />
          {renderContent()}
        </div>
      </main>

      {showAddModal && (
        <div className="modal-overlay" role="dialog" aria-modal="true">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Add {modalType}</h2>
              <button className="close-btn" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              <p>Form goes here…</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
