import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaUsers, FaChalkboardTeacher, FaBuilding, FaBook, 
  FaClipboardCheck, FaChartBar, FaCog, FaSignOutAlt,
  FaPlus, FaEdit, FaTrash, FaEye, FaSearch, FaFilter,
  FaBell, FaUserCircle, FaChevronDown, FaDownload,
  FaCalendarAlt, FaGraduationCap, FaAward, FaClock
} from 'react-icons/fa';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('overview');
  const [showAddModal, setShowAddModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [students, setStudents] = useState([]);
  const [faculty, setFaculty] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState('');
  const [adminName, setAdminName] = useState('Administrator');

  const handleLogout = () => {
    navigate('/');
  };

  const handleAddClick = (type) => {
    setModalType(type);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setModalType('');
  };

  const navigationItems = [
    { 
      id: 'overview', 
      label: 'Overview', 
      icon: FaChartBar, 
      description: 'Dashboard analytics and key metrics',
      color: '#667eea'
    },
    { 
      id: 'students', 
      label: 'Students', 
      icon: FaUsers, 
      description: 'Manage student records and enrollment',
      color: '#48bb78'
    },
    { 
      id: 'faculty', 
      label: 'Faculty', 
      icon: FaChalkboardTeacher, 
      description: 'Faculty profiles and assignments',
      color: '#ed8936'
    },
    { 
      id: 'departments', 
      label: 'Departments', 
      icon: FaBuilding, 
      description: 'Department structure and management',
      color: '#9f7aea'
    },
    { 
      id: 'courses', 
      label: 'Courses', 
      icon: FaBook, 
      description: 'Course catalog and curriculum',
      color: '#f56565'
    },
    { 
      id: 'attendance', 
      label: 'Attendance', 
      icon: FaClipboardCheck, 
      description: 'Attendance tracking and reports',
      color: '#38b2ac'
    },
    { 
      id: 'notices', 
      label: 'Notices', 
      icon: FaBell, 
      description: 'Manage notices and announcements',
      color: '#f6ad55'
    },
    { 
      id: 'reports', 
      label: 'Reports', 
      icon: FaChartBar, 
      description: 'Analytics and insights',
      color: '#4299e1'
    },
    { 
      id: 'settings', 
      label: 'Settings', 
      icon: FaCog, 
      description: 'System configuration',
      color: '#718096'
    }
  ];

  const activeMeta = useMemo(() => navigationItems.find(n => n.id === activeSection) || navigationItems[0], [activeSection]);

  // Fetch admin name on component mount
  useEffect(() => {
    const fetchAdminName = async () => {
      try {
        const adminCode = localStorage.getItem('adminCode');
        if (adminCode) {
          const response = await fetch('http://localhost:9090/api/neo/verse/admin/getAdminName', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              adminCode: parseInt(adminCode)
            })
          });

          const data = await response.json();
          if (data.status === 1 && data.data && data.data.adminName) {
            setAdminName(data.data.adminName);
          }
        }
      } catch (error) {
        console.error('Error fetching admin name:', error);
        // Keep default name if API fails
      }
    };

    fetchAdminName();
  }, []);

  // Demo data initialization
  useEffect(() => {
    setLoading(true);
    const timer = setTimeout(() => {
      setStudents([
        { id: 1, name: 'Aarav Patel', email: 'aarav@tat.edu', department: 'CSE', year: '3', status: 'active', gpa: '8.5' },
        { id: 2, name: 'Isha Sharma', email: 'isha@tat.edu', department: 'ECE', year: '2', status: 'active', gpa: '9.2' },
        { id: 3, name: 'Rohit Das', email: 'rohit@tat.edu', department: 'ME', year: '4', status: 'active', gpa: '7.8' },
        { id: 4, name: 'Priya Singh', email: 'priya@tat.edu', department: 'CSE', year: '1', status: 'active', gpa: '8.9' },
        { id: 5, name: 'Vikram Kumar', email: 'vikram@tat.edu', department: 'ECE', year: '3', status: 'inactive', gpa: '7.2' }
      ]);
      setFaculty([
        { id: 1, name: 'Prof. Meera Nair', email: 'meera@tat.edu', department: 'CSE', position: 'Associate Professor', experience: '12 years' },
        { id: 2, name: 'Dr. Ajay Kumar', email: 'ajay@tat.edu', department: 'ECE', position: 'Professor', experience: '15 years' },
        { id: 3, name: 'Dr. Sunita Rao', email: 'sunita@tat.edu', department: 'ME', position: 'Assistant Professor', experience: '8 years' }
      ]);
      setDepartments([
        { id: 1, name: 'Computer Science & Engineering', head: 'Dr. S. Rao', students: 420, faculty: 28, established: '2010' },
        { id: 2, name: 'Electronics & Communication', head: 'Dr. P. Nanda', students: 310, faculty: 22, established: '2012' },
        { id: 3, name: 'Mechanical Engineering', head: 'Dr. R. Sharma', students: 280, faculty: 18, established: '2015' }
      ]);
      setCourses([
        { id: 101, name: 'Data Structures & Algorithms', department: 'CSE', credits: 4, instructor: 'Prof. Meera Nair', students: 45 },
        { id: 205, name: 'Digital Signal Processing', department: 'ECE', credits: 3, instructor: 'Dr. Ajay Kumar', students: 38 },
        { id: 301, name: 'Thermodynamics', department: 'ME', credits: 3, instructor: 'Dr. Sunita Rao', students: 42 }
      ]);
      setLoading(false);
    }, 800);
    return () => clearTimeout(timer);
  }, []);

  const filteredStudents = useMemo(() => {
    if (!query) return students;
    const q = query.toLowerCase();
    return students.filter(s => (
      s.name.toLowerCase().includes(q) ||
      s.email.toLowerCase().includes(q) ||
      s.department.toLowerCase().includes(q) ||
      String(s.id).includes(q)
    ));
  }, [students, query]);

  const filteredFaculty = useMemo(() => {
    if (!query) return faculty;
    const q = query.toLowerCase();
    return faculty.filter(f => (
      f.name.toLowerCase().includes(q) ||
      f.email.toLowerCase().includes(q) ||
      f.department.toLowerCase().includes(q) ||
      f.position.toLowerCase().includes(q)
    ));
  }, [faculty, query]);

  const PageHeader = () => (
    <div className="page-header">
      <div className="page-header-left">
        <div className="page-title">
          <div className="title-icon" style={{ backgroundColor: activeMeta.color }}>
            <activeMeta.icon />
          </div>
          <div className="title-content">
            <h1>{activeMeta.label}</h1>
            <p>{activeMeta.description}</p>
          </div>
        </div>
      </div>
      <div className="page-header-right">
        <div className="header-actions">
          {activeSection !== 'overview' && (activeSection === 'students' || activeSection === 'faculty' || activeSection === 'departments' || activeSection === 'courses') && (
            <button className="primary-btn" onClick={() => handleAddClick(activeSection.slice(0, -1))}>
              <FaPlus />
              Add {activeMeta.label.slice(0, -1)}
            </button>
          )}
          {activeSection === 'reports' && (
            <button className="secondary-btn">
              <FaDownload />
              Export
            </button>
          )}
        </div>
      </div>
    </div>
  );

  const NotificationPanel = () => (
    <div className="notification-panel">
      <div className="notification-header">
        <FaBell />
        <span>Notifications</span>
        <span className="notification-count">{notifications.length}</span>
      </div>
      <div className="notification-list">
        {notifications.map(notification => (
          <div key={notification.id} className={`notification-item ${notification.type}`}>
            <div className="notification-content">
              <p>{notification.message}</p>
              <span className="notification-time">{notification.time}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const EmptyState = ({ title, subtitle, icon: Icon }) => (
    <div className="empty-state">
      <div className="empty-icon">
        <Icon />
      </div>
      <h3>{title}</h3>
      <p>{subtitle}</p>
    </div>
  );

  const LoadingState = () => (
    <div className="loading-state">
      <div className="spinner" />
      <p>Loading {activeMeta.label.toLowerCase()}...</p>
    </div>
  );

  const SearchBar = ({ placeholder }) => (
    <div className="search-bar">
      <FaSearch />
      <input
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder={placeholder}
      />
      {query && (
        <button className="clear-search" onClick={() => setQuery('')}>
          ×
        </button>
      )}
    </div>
  );

  const StatCard = ({ title, value, icon: Icon, color, trend, subtitle }) => (
    <div className="stat-card">
      <div className="stat-header">
        <div className="stat-icon" style={{ backgroundColor: color }}>
          <Icon />
        </div>
        <div className="stat-trend" style={{ color: trend > 0 ? '#48bb78' : '#f56565' }}>
          {trend > 0 ? '+' : ''}{trend}%
        </div>
      </div>
      <div className="stat-content">
        <h3>{value}</h3>
        <p>{title}</p>
        {subtitle && <span className="stat-subtitle">{subtitle}</span>}
      </div>
    </div>
  );

  const renderOverview = () => (
    <div className="overview-section">
      <div className="stats-grid">
        <StatCard 
          title="Total Students" 
          value={students.length} 
          icon={FaUsers} 
          color="#48bb78" 
          trend={12}
          subtitle="Active enrollments"
        />
        <StatCard 
          title="Faculty Members" 
          value={faculty.length} 
          icon={FaChalkboardTeacher} 
          color="#ed8936" 
          trend={5}
          subtitle="Teaching staff"
        />
        <StatCard 
          title="Departments" 
          value={departments.length} 
          icon={FaBuilding} 
          color="#9f7aea" 
          trend={0}
          subtitle="Academic units"
        />
        <StatCard 
          title="Active Courses" 
          value={courses.length} 
          icon={FaBook} 
          color="#f56565" 
          trend={8}
          subtitle="This semester"
        />
      </div>

      <div className="overview-content">
        <div className="overview-left">
          <div className="content-card">
            <div className="card-header">
              <h3>Recent Activities</h3>
              <button className="view-all-btn">View All</button>
            </div>
            <div className="activity-list">
              <div className="activity-item">
                <div className="activity-icon">
                  <FaUserCircle />
                </div>
                <div className="activity-content">
                  <p>New student Priya Singh enrolled in CSE</p>
                  <span>2 hours ago</span>
                </div>
              </div>
              <div className="activity-item">
                <div className="activity-icon">
                  <FaGraduationCap />
                </div>
                <div className="activity-content">
                  <p>Course "Data Structures" updated</p>
                  <span>4 hours ago</span>
                </div>
              </div>
              <div className="activity-item">
                <div className="activity-icon">
                  <FaAward />
                </div>
                <div className="activity-content">
                  <p>Faculty performance review completed</p>
                  <span>1 day ago</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="overview-right">
          <NotificationPanel />
        </div>
      </div>
    </div>
  );

  const renderStudents = () => (
    <div className="section">
      <div className="section-toolbar">
        <SearchBar placeholder="Search students by name, email, department or ID" />
        <div className="toolbar-actions">
          <button className="filter-btn">
            <FaFilter />
            Filter
          </button>
        </div>
      </div>
      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
              <th>Year</th>
              <th>GPA</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="8"><LoadingState /></td></tr>
            ) : filteredStudents.length === 0 ? (
              <tr><td colSpan="8"><EmptyState title="No students found" subtitle="Try adjusting your search criteria" icon={FaUsers} /></td></tr>
            ) : (
              filteredStudents.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>
                    <div className="user-info">
                      <div className="user-avatar">
                        {student.name.split(' ').map(n => n[0]).join('')}
                      </div>
                      <span>{student.name}</span>
                    </div>
                  </td>
                  <td>{student.email}</td>
                  <td>
                    <span className="department-badge">{student.department}</span>
                  </td>
                  <td>{student.year}</td>
                  <td>
                    <span className="gpa-badge">{student.gpa}</span>
                  </td>
                  <td>
                    <span className={`status-badge ${student.status}`}>
                      {student.status}
                    </span>
                  </td>
                  <td className="actions">
                    <button className="action-btn view" title="View Details">
                      <FaEye />
                    </button>
                    <button className="action-btn edit" title="Edit Student">
                      <FaEdit />
                    </button>
                    <button className="action-btn delete" title="Delete Student">
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderFaculty = () => (
    <div className="section">
      <div className="section-toolbar">
        <SearchBar placeholder="Search faculty by name, email, department or position" />
        <div className="toolbar-actions">
          <button className="filter-btn">
            <FaFilter />
            Filter
          </button>
        </div>
      </div>
      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
              <th>Position</th>
              <th>Experience</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="7"><LoadingState /></td></tr>
            ) : filteredFaculty.length === 0 ? (
              <tr><td colSpan="7"><EmptyState title="No faculty found" subtitle="Try adjusting your search criteria" icon={FaChalkboardTeacher} /></td></tr>
            ) : (
              filteredFaculty.map((member) => (
                <tr key={member.id}>
                  <td>{member.id}</td>
                  <td>
                    <div className="user-info">
                      <div className="user-avatar">
                        {member.name.split(' ').map(n => n[0]).join('')}
                      </div>
                      <span>{member.name}</span>
                    </div>
                  </td>
                  <td>{member.email}</td>
                  <td>
                    <span className="department-badge">{member.department}</span>
                  </td>
                  <td>{member.position}</td>
                  <td>{member.experience}</td>
                  <td className="actions">
                    <button className="action-btn view" title="View Details">
                      <FaEye />
                    </button>
                    <button className="action-btn edit" title="Edit Faculty">
                      <FaEdit />
                    </button>
                    <button className="action-btn delete" title="Delete Faculty">
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderDepartments = () => (
    <div className="section">
      <div className="departments-grid">
        {loading ? (
          <LoadingState />
        ) : departments.length === 0 ? (
          <EmptyState title="No departments" subtitle="Click Add Department to create one" icon={FaBuilding} />
        ) : (
          departments.map((dept) => (
            <div key={dept.id} className="department-card">
              <div className="department-header">
                <div className="department-icon">
                  <FaBuilding />
                </div>
                <div className="department-info">
                  <h3>{dept.name}</h3>
                  <p>Est. {dept.established}</p>
                </div>
              </div>
              <div className="department-stats">
                <div className="stat">
                  <span className="stat-value">{dept.students}</span>
                  <span className="stat-label">Students</span>
                </div>
                <div className="stat">
                  <span className="stat-value">{dept.faculty}</span>
                  <span className="stat-label">Faculty</span>
                </div>
              </div>
              <div className="department-footer">
                <p><strong>Head:</strong> {dept.head}</p>
                <div className="department-actions">
                  <button className="action-btn view" title="View Details">
                    <FaEye />
                  </button>
                  <button className="action-btn edit" title="Edit Department">
                    <FaEdit />
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );

  const renderCourses = () => (
    <div className="section">
      <div className="courses-grid">
        {loading ? (
          <LoadingState />
        ) : courses.length === 0 ? (
          <EmptyState title="No courses" subtitle="Click Add Course to create one" icon={FaBook} />
        ) : (
          courses.map((course) => (
            <div key={course.id} className="course-card">
              <div className="course-header">
                <div className="course-code">{course.id}</div>
                <div className="course-credits">{course.credits} Credits</div>
              </div>
              <div className="course-content">
                <h3>{course.name}</h3>
                <p className="course-department">{course.department}</p>
                <p className="course-instructor">
                  <FaChalkboardTeacher />
                  {course.instructor}
                </p>
                <div className="course-stats">
                  <span className="enrolled-students">
                    <FaUsers />
                    {course.students} students
                  </span>
                </div>
              </div>
              <div className="course-actions">
                <button className="action-btn view" title="View Details">
                  <FaEye />
                </button>
                <button className="action-btn edit" title="Edit Course">
                  <FaEdit />
                </button>
                <button className="action-btn delete" title="Delete Course">
                  <FaTrash />
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );

  const renderAttendance = () => (
    <div className="attendance-section">
      <div className="attendance-stats">
        <StatCard 
          title="Today's Attendance" 
          value="85%" 
          icon={FaClock} 
          color="#38b2ac" 
          trend={3}
          subtitle="Overall average"
        />
        <StatCard 
          title="This Week" 
          value="92%" 
          icon={FaCalendarAlt} 
          color="#4299e1" 
          trend={5}
          subtitle="Weekly average"
        />
        <StatCard 
          title="This Month" 
          value="88%" 
          icon={FaChartBar} 
          color="#9f7aea" 
          trend={-2}
          subtitle="Monthly average"
        />
      </div>
      <div className="attendance-content">
        <div className="content-card">
          <div className="card-header">
            <h3>Attendance Trends</h3>
            <button className="view-all-btn">View Details</button>
          </div>
          <div className="attendance-chart">
            <p>Attendance chart visualization would go here</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderReports = () => (
    <div className="reports-section">
      <div className="reports-grid">
        <div className="report-card">
          <div className="report-icon">
            <FaUsers />
          </div>
          <h3>Student Performance</h3>
          <p>Comprehensive analysis of student academic performance across all departments</p>
          <button className="report-btn">Generate Report</button>
        </div>
        <div className="report-card">
          <div className="report-icon">
            <FaClipboardCheck />
          </div>
          <h3>Attendance Reports</h3>
          <p>Detailed attendance tracking and analysis for all courses and students</p>
          <button className="report-btn">Generate Report</button>
        </div>
        <div className="report-card">
          <div className="report-icon">
            <FaChartBar />
          </div>
          <h3>Financial Reports</h3>
          <p>Financial summaries, fee collection, and budget analysis reports</p>
          <button className="report-btn">Generate Report</button>
        </div>
        <div className="report-card">
          <div className="report-icon">
            <FaGraduationCap />
          </div>
          <h3>Academic Reports</h3>
          <p>Course completion rates, grade distributions, and academic insights</p>
          <button className="report-btn">Generate Report</button>
        </div>
      </div>
    </div>
  );

  const renderSettings = () => (
    <div className="settings-section">
      <div className="settings-grid">
        <div className="setting-card">
          <div className="setting-icon">
            <FaCog />
          </div>
          <h3>General Settings</h3>
          <p>Configure system preferences, themes, and general application settings</p>
          <button className="setting-btn">Configure</button>
        </div>
        <div className="setting-card">
          <div className="setting-icon">
            <FaUsers />
          </div>
          <h3>User Management</h3>
          <p>Manage user accounts, roles, permissions, and access controls</p>
          <button className="setting-btn">Manage Users</button>
        </div>
        <div className="setting-card">
          <div className="setting-icon">
            <FaDownload />
          </div>
          <h3>Backup & Restore</h3>
          <p>System backup, data export, and restore functionality</p>
          <button className="setting-btn">Backup Now</button>
        </div>
        <div className="setting-card">
          <div className="setting-icon">
            <FaBell />
          </div>
          <h3>Notifications</h3>
          <p>Configure notification preferences and alert settings</p>
          <button className="setting-btn">Configure</button>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return renderOverview();
      case 'students':
        return renderStudents();
      case 'faculty':
        return renderFaculty();
      case 'departments':
        return renderDepartments();
      case 'courses':
        return renderCourses();
      case 'attendance':
        return renderAttendance();
      case 'reports':
        return renderReports();
      case 'settings':
        return renderSettings();
      default:
        return renderOverview();
    }
  };

  return (
    <div className="admin-dashboard">
      {/* Top Header */}
      <header className="dashboard-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo">
              <span>TAT</span>
            </div>
            <h1>Admin Panel</h1>
          </div>
          <div className="header-right">
            <div className="profile-section">
              <div className="profile-avatar">
                <FaUserCircle />
              </div>
              <div className="profile-info">
                <span className="profile-name">{adminName}</span>
                <span className="profile-designation">Admin</span>
              </div>
            </div>
            <button className="logout-btn" onClick={handleLogout}>
              <FaSignOutAlt />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </header>

      {/* Horizontal Navigation */}
      <nav className="horizontal-nav">
        {navigationItems.map((item) => (
          <button
            key={item.id}
            className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
            onClick={() => setActiveSection(item.id)}
            style={{ '--nav-color': item.color }}
          >
            <item.icon />
            <span>{item.label}</span>
          </button>
        ))}
      </nav>

      {/* Main Content */}
      <main className="main-content">
        <div className="dashboard-content">
          <PageHeader />
          {renderContent()}
        </div>
      </main>

      {/* Add Modal */}
      {showAddModal && (
        <div className="modal-overlay" role="dialog" aria-modal="true">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Add {modalType}</h2>
              <button className="close-btn" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body">
              <p>Form for adding {modalType} will be implemented here...</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
