export default function Courses() {
  const courses = [
    { title: "Computer Science", desc: "Learn coding, AI, and modern tech." },
    { title: "Business Management", desc: "Master leadership and entrepreneurship." },
    { title: "Engineering", desc: "Innovate with cutting-edge engineering solutions." },
  ];

  return (
    <section className="py-16 px-6 text-center">
      <h2 className="text-3xl font-bold mb-8">Our Courses</h2>
      <div className="grid md:grid-cols-3 gap-8">
        {courses.map((course, index) => (
          <div key={index} className="bg-white shadow-lg p-6 rounded-lg hover:scale-105 transition">
            <h3 className="text-xl font-bold mb-2">{course.title}</h3>
            <p className="text-gray-600">{course.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
