import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { FaSignOutAlt, FaUser, FaBook, FaClipboardCheck, FaChartBar, FaUsers } from 'react-icons/fa';
import './FacultyDashboard.css';

const FacultyDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, logout } = useAuth();
  const [activeSection, setActiveSection] = useState('overview');

  // Check if user is authenticated
  useEffect(() => {
    if (!currentUser) {
      navigate('/faculty-login');
    }
  }, [currentUser, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const navigationItems = [
    { id: 'overview', label: 'Overview', icon: FaChartBar },
    { id: 'profile', label: 'Profile', icon: FaUser },
    { id: 'courses', label: 'My Courses', icon: FaBook },
    { id: 'students', label: 'My Students', icon: FaUsers },
    { id: 'attendance', label: 'Attendance', icon: FaClipboardCheck },
    { id: 'results', label: 'Results', icon: FaChartBar }
  ];

  const renderOverview = () => (
    <div className="overview-section">
      <h2>Faculty Dashboard</h2>
      <div className="welcome-card">
        <h3>Welcome, {currentUser?.name}!</h3>
        <p>Department: {currentUser?.department}</p>
        <p>Designation: {currentUser?.designation}</p>
      </div>
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">
            <FaBook />
          </div>
          <div className="stat-content">
            <h3>4</h3>
            <p>Assigned Courses</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">
            <FaUsers />
          </div>
          <div className="stat-content">
            <h3>120</h3>
            <p>Total Students</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">
            <FaClipboardCheck />
          </div>
          <div className="stat-content">
            <h3>95%</h3>
            <p>Classes Taken</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return renderOverview();
      default:
        return (
          <div className="section-content">
            <h2>{navigationItems.find(item => item.id === activeSection)?.label}</h2>
            <p>This section will be implemented soon.</p>
          </div>
        );
    }
  };

  if (!currentUser) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="faculty-dashboard">
      {/* Sidebar Navigation */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <span>TAT</span>
          </div>
          <h3>Faculty Panel</h3>
        </div>
        
        <nav className="sidebar-nav">
          {navigationItems.map((item) => (
            <button
              key={item.id}
              className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => setActiveSection(item.id)}
            >
              <item.icon />
              <span>{item.label}</span>
            </button>
          ))}
        </nav>
        
        <div className="sidebar-footer">
          <button className="logout-btn" onClick={handleLogout}>
            <FaSignOutAlt />
            <span>Logout</span>
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        <header className="content-header">
          <div className="header-info">
            <h1>{navigationItems.find(item => item.id === activeSection)?.label}</h1>
            <p>Welcome back, {currentUser.name}</p>
          </div>
          <div className="user-info">
            <span className="user-avatar">{currentUser.name.charAt(0)}</span>
            <span className="user-name">{currentUser.name}</span>
          </div>
        </header>

        <div className="content-body">
          {renderContent()}
        </div>
      </main>
    </div>
  );
};

export default FacultyDashboard;
